import re

from bs4 import BeautifulSoup

from debug import debug


def like_text_num(text):
    if str(text).__contains__("K"):
        text = float(str(text).split("K")[0]) * 1000
        return int(text)

    else:
        return text


def comment_text_num(text):
    text = str(text).split(" ")[0]
    if str(text).__contains__("K"):
        text = float(str(text).split("K")[0]) * 1000
    return str(int(text))


def share_text_num(text):
    num_list = re.findall(r'-?\d+\.?\d*', text)
    num = ""
    for x in num_list:
        num = num + str(x)
    return str(num)


def link_parser(link):
    list_str = link.split("%2F")[1:-1]
    new_str = ""
    for i in list_str:
        if new_str.__len__() == 0:
            new_str = i
        else:
            new_str = new_str + "/" + i
    return "https://" + new_str


class PostParser:
    def __init__(self, div):
        self.dbag = debug(name=self.__class__, flag=True)
        self.soup = BeautifulSoup(div, 'lxml')

    def date(self):
        try:
            post_date = self.div_data_getter(tag="abbr", tag_class="livetimestamp", text=False, tag_value="title")
            self.dbag.debug_print("Post's Date: " + str(post_date))
        except Exception as e:
            post_date = "None"
            self.dbag.debug_print("Error in Function date\n" + str(e))
        return post_date

    def likes_count(self):
        try:
            like_text = self.div_data_getter(tag="span", tag_class="_4arz")
            like = like_text_num(like_text)
            self.dbag.debug_print("Post's Likes: " + str(like))
        except Exception as e:
            like = 0
            self.dbag.debug_print("Error in Function likes_count\n " + str(e))
        return like

    def comments_count(self):
        try:
            comment_text = self.div_data_getter(tag="a", tag_class="_ipm _-56")
            comment = comment_text_num(comment_text)
            self.dbag.debug_print("Post's Comments: " + str(comment))
        except Exception as e:
            comment = 0
            self.dbag.debug_print("Error in Function comments_count\n " + str(e))
        return comment

    def share_count(self):
        try:
            share_text = self.div_data_getter(tag="a", tag_class="_ipm _2x0m")
            share = share_text_num(share_text)
            self.dbag.debug_print("Post's Share: " + str(share))
        except Exception as e:
            share = 0
            self.dbag.debug_print("Error in Function share_count\n " + str(e))
        return share

    def public_post_title(self):
        try:
            title = self.div_data_getter(tag="div", tag_class="mbs _6m6 _2cnj _5s6c")
            self.dbag.debug_print("Post's Title: " + title)
        except Exception as e:
            title = 0
            self.dbag.debug_print("Error in Function title\n " + str(e))
        return title

    def public_post_subtitle(self):
        try:
            sub_title = self.div_data_getter(tag="div", tag_class="_6m7 _3bt9")
            self.dbag.debug_print("Post's Subtitle: " + sub_title)
        except Exception as e:
            sub_title = 0
            self.dbag.debug_print("Error in Function sub_title\n " + str(e))
        return sub_title

    def public_post_url(self):
        try:
            post_url = self.div_data_getter(tag="a", tag_class="_52c6", text=False, tag_value="href")
            post_url = link_parser(post_url)
            self.dbag.debug_print("Post's Post URL: " + post_url)
        except Exception as e:
            post_url = "Link not found"
            self.dbag.debug_print("Error in Function public_post_url\n " + str(e))
        return post_url

    def div_data_getter(self, tag, tag_class, text=True, tag_value=""):
        if text:
            data = self.soup.find_all(tag, {"class": tag_class})[0].text
        else:
            data = self.soup.find_all(tag, {"class": tag_class})
            data = data[0].get(tag_value)
        return data


def Test(div):
    PP = PostParser(div)
    print(PP.public_post_title())
    print(PP.public_post_subtitle())
    print(PP.date())
    print(PP.likes_count())
    print(PP.comments_count())
    print(PP.share_count())
    print(PP.public_post_url())

